import React, { useEffect } from "react";

const Modal = ({ isOpen, onClose, userData }) => {
  useEffect(() => {
    if (isOpen) {
      const modal = new window.bootstrap.Modal(document.getElementById('example'));
      modal.show();
    }
  }, [isOpen]);

  const style={
    color:"#AF54FF"
  }

  return (
    <div
      className="modal fade"
      id="example"
      tabIndex="-1"
      aria-labelledby="exampleModalLabel"
      aria-hidden="true">
      <div className="modal-dialog">
        <div className="modal-content">
          <div className="modal-header">
            <h1 className="modal-title fs-5" id="exampleModalLabel"><b>User Information</b></h1>
            <button
              type="button"
              className="btn-close"
              data-bs-dismiss="modal"
              aria-label="Close"
              onClick={onClose}></button>
          </div>
          <div className="modal-body">
            {userData ? (
              <div>
                <p><strong style={style}>Name:</strong> <span style={{color:""}}>{userData.name}</span></p>
                <p><strong style={style}>Email:</strong> <span>{userData.email}</span></p>
                <p><strong style={style}>Date:</strong> <span>{new Date(userData.date).toLocaleString()}</span></p>
              </div>
            ) : (
              <p>Loading...</p>
            )}
          </div>
          <div className="modal-footer">
            <button
              type="button"
              className="btn btn-secondary"
              data-bs-dismiss="modal"
              onClick={onClose}>
              Close
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Modal;
