import React, { useEffect } from 'react';
import './About.css';

const About = () => {
  useEffect(() => {
    document.body.style.background = 'linear-gradient(to right, #ff7e5f, #feb47b)';
    document.body.style.height = '100vh';
    document.body.style.margin = '0';
    document.body.style.fontFamily = 'Arial, sans-serif';

    // Cleanup function to remove the styles when the component unmounts
    return () => {
      document.body.style.background = '';
      document.body.style.height = '';
      document.body.style.margin = '';
      document.body.style.fontFamily = '';
    };
  }, []);

  return (
    <div className="about-container">
      <h1 className="about-title">Welcome to iNotebook!</h1>
      <p className="about-content">
        iNotebook is a modern and secure cloud-based application designed to help you store, manage, and access your notes from anywhere. Built with the powerful MERN stack (<span className="about-highlight">MongoDB</span>, <span className="about-highlight">Express.js</span>, <span className="about-highlight">React</span>, and <span className="about-highlight">Node.js</span>), iNotebook provides a seamless experience for users to keep their thoughts and ideas organized.
      </p>
      <p className="about-content">
        <span className="about-highlight">Secure User Authentication</span>: Create a new account, securely log in, and keep your notes safe.
      </p>
      <p className="about-content">
        <span className="about-highlight">Note Management</span>: Add, edit, delete, and search your notes effortlessly.
      </p>
      <p className="about-content">
        <span className="about-highlight">Cloud Storage</span>: Access your notes from any device with real-time sync.
      </p>
      <p className="about-content">
        <span className="about-highlight">User-Friendly Interface</span>: Enjoy a responsive and intuitive design.
      </p>
      <p className="about-content">
        <span className="about-highlight">Data Security</span>: Rest assured, your data is protected with encryption and secure communication.
      </p>
    </div>
  );
};

export default About;
